export function mediaThumbnailChanged(event) {
    // Get the primary color from the media thumbnail
    let primaryColor = event.primaryColor;

    // Calculate the opposite color with less white
    let oppositeColor = new Vec3(0.7 - primaryColor.x, 0.7 - primaryColor.y, 0.7 - primaryColor.z);

    // Set the target color to the opposite color
    targetColor = oppositeColor;
}

