text.script:
'use strict';

/**
 * @param {String} value - for property 'text'
 * @return {String} - update current property value
 */


var PredictedMediaTime = 0
var CurrentUpdateMediaTime = 0
var TotalDuration = 0

// Media is Playing: 1
// Media is Paused: 2
// Media is Stopped: 0
var currentState = 0

function ConvertSecondToMinSecTxt(seconds){

	var minutes = new Number(Math.floor(seconds / 60));
	seconds = new Number(Math.floor(seconds - minutes * 60));

	if (seconds < 10){
		seconds = "0" + seconds
	}

	return minutes + ":" + seconds

}

/**
 * @param {MediaPlaybackEvent} event
 */
export function mediaPlaybackChanged(event) {
	currentState = event.state;
}



export function mediaTimelineChanged(event) {

	CurrentUpdateMediaTime = event.position
	TotalDuration = event.duration
	PredictedMediaTime = CurrentUpdateMediaTime
	thisLayer.text = ConvertSecondToMinSecTxt(CurrentUpdateMediaTime)
}


export function update(value) {

	if (currentState == MediaPlaybackEvent.PLAYBACK_PLAYING) {
		PredictedMediaTime += engine.frametime
		if (PredictedMediaTime >= TotalDuration){
			PredictedMediaTime = TotalDuration
		}
		return ConvertSecondToMinSecTxt(PredictedMediaTime)
	} else if (currentState == MediaPlaybackEvent.PLAYBACK_PAUSED) {
		//pass
		//console.log("not playing")
	} else {
		//AccumulateFrameTime = 0
		PredictedMediaTime = 0
	}

	return value;
}

