scale.script:
'use strict';
// Please note: Do not remove this line or asset references may break.
export let __workshopId = '2877039332';
import * as WEMath from 'WEMath';

//--Global Data--
// Your duration bar's x scale
var userScale = 1;
var MaxScale = 1.45;
var CurrentScale = 0;

// How long media is into the track in seconds
var position = 0;

// How long media is in seconds
var duration = 0;

// Media is Playing: 1
// Media is Paused: 2
// Media is Stopped: 0
var currentState = 0;

var AccumulateFrameTime = 0
var AccumulateDuration = 1

//--------------------------------------------------------------------------------------------
export function applyUserProperties(changedUserProperties) {
	if (changedUserProperties.hasOwnProperty('scale')) {
        // Assign the changed property to a local variable
        userScale = changedUserProperties.scale;
		thisLayer.scale.x = (position/duration) * MaxScale * userScale 
		thisLayer.scale.y = 0.05 * userScale
    }
}


/**
 * @param {Vec3} value - for property 'scale'
 * @return {Vec3} - update current property value
 */
export function update(value) {
	// The bar will move while a song is playing, if paused it will adjust to where the user 		moves the position of the song to 
	if (currentState == MediaPlaybackEvent.PLAYBACK_PLAYING) {
		// Thank you https://steamcommunity.com/id/XerRag
		// position += engine.frametime;
		// value = new Vec3(WEMath.smoothStep(0, duration, position) * scaleX, scaleY, scaleZ)
		// position += engine.frametime;
		// console.log(engine.frametime)
		AccumulateFrameTime += engine.frametime
		if (AccumulateFrameTime >= AccumulateDuration){
			position += AccumulateFrameTime
			AccumulateFrameTime = 0
		}
		if (position >= duration){
			position = duration
		}
		value.x = (position/duration) * MaxScale * userScale 
	} else if (currentState == MediaPlaybackEvent.PLAYBACK_PAUSED) {
		// value = new Vec3(WEMath.smoothStep(0, duration, position) * scaleX, scaleY, scaleZ)
		value.x = (position/duration) * MaxScale * userScale 
	} else {
		AccumulateFrameTime = 0
	}
	return value;
}

/**
 * @param {MediaTimelineEvent} event
 */
export function mediaTimelineChanged(event) {
	position = event.position;
	duration = event.duration;
}

/**
 * @param {MediaPlaybackEvent} event
 */
export function mediaPlaybackChanged(event) {
	currentState = event.state;
}

