// Media is Stopped: 0
var LastMusicState = 0
var CurrentMusicState = 0


var StartFadeOut = false
var StartFadeIn = false


var delayCounter = 0
var LastdelayTotalCount = 0
var EnableDelay = false
var AnimationDelayDuration

var MaxAlpha
const MinAlpha = 0

var HalfX
var HalfY

var UserProperties
