text.script:
'use strict';
// Please note: Do not remove this line or asset references may break.
export let __workshopId = '2978738836';
// Please note: Do not remove this line or asset references may break.
// Please note: Do not remove this line or asset references may break.
// Please note: Do not remove this line or asset references may break.
// Please note: Do not remove this line or asset references may break.

/**
 * @param {MediaPropertiesEvent} event
 */

var currentTitle = ""
var animatedCounter = 0
var scrollCooldownCounter = 0
var scrollCooldownduration = 20
var enableScrollCoolDown = false
var enableAnimatedScroll = false
var displayTitle = ""
var displayLength = 593
var enablePauseEnd = false
var pauseEndCounter = 0
var pauseEndDuration = 200
var pauseDuration = 600
var pauseCounter = 0
var enablePause = false


var EnableTypeInAnimation = false
var EnableTypingDelay = false
var TypingDelayCount = 0
var TypingDelayDuration
var CurretTypedIndex = 0
var MaxTypingDelay = 20
var MinTypingDelay = 10

var EnableTypingMistake = true
var StartTypeInAnimation
var Typingmistakechance = 0.5
var MistakeWordCount = 0
var MaxMistakeWord = 3
var TotalMistakewords = 0
var StartMistakeWord = false
var StartDeleteMistake = false
var IncorrectWord = ""
var PossibleMistakeString = "1234567890-=qwertyuiop[]asdfghjkl;'zxcvbnm,./"

// // Media is Playing: 1
// // Media is Paused: 2
// // Media is Stopped: 0
var LastMusicState = 0
var CurrentMusicState = 0

var enablePauseAtStart = false


export function applyUserProperties(changedUserProperties) {
        if (changedUserProperties.hasOwnProperty('maxtypingdelay')) {
			MaxTypingDelay = changedUserProperties.maxtypingdelay
		}
		if (changedUserProperties.hasOwnProperty('mintypingdelay')) {
			MinTypingDelay = changedUserProperties.mintypingdelay
		}
		if (changedUserProperties.hasOwnProperty('typingmistakechance')){
			Typingmistakechance = changedUserProperties.typingmistakechance
		}
		if (changedUserProperties.hasOwnProperty("enabletypingmistake")){
			EnableTypingMistake = changedUserProperties.enabletypingmistake
		}
		if (changedUserProperties.hasOwnProperty("enabletypinganimation")){
			EnableTypeInAnimation = changedUserProperties.enabletypinganimation
		}
		if (changedUserProperties.hasOwnProperty("maxmistakeword")){
			MaxMistakeWord = changedUserProperties.maxmistakeword
		}
}

function initTypingDelay(factor){
	EnableTypingDelay = true
	TypingDelayCount = 0
	TypingDelayDuration = new Number(Math.random() * (MaxTypingDelay - MinTypingDelay) * factor) + MinTypingDelay
	// console.log("delay",TypingDelayDuration)
}

export function mediaPlaybackChanged(event) {
	CurrentMusicState = event.state
}

export function mediaPropertiesChanged(event) {
	currentTitle = event.title;
	displayTitle = currentTitle

	enablePauseAtStart = true

	if (EnableTypeInAnimation) {
		if (MinTypingDelay >= MaxTypingDelay){
			var temp = MinTypingDelay
			MinTypingDelay = MaxTypingDelay
			MaxTypingDelay = temp
		}
		TypingDelayDuration = new Number(Math.random() * (MaxTypingDelay - MinTypingDelay)) + MinTypingDelay
		CurretTypedIndex = 0
		StartTypeInAnimation = true
		initTypingDelay(1)
	}
}

export function update(value) {
	var dummySongTitleSizeX = thisScene.getLayer("DummySongTitle").size.x
	enableAnimatedScroll = false
	if (dummySongTitleSizeX > thisLayer.maxwidth){
		enableAnimatedScroll = true
		if (enablePauseAtStart == true){
			enablePauseAtStart = false
			enablePause = true
		}
	}
	// console.log(dummySongTitleSizeX)
	if (CurrentMusicState == MediaPlaybackEvent.PLAYBACK_PLAYING){
		if (LastMusicState != CurrentMusicState){
			LastMusicState = CurrentMusicState
			if (EnableTypeInAnimation){
				CurretTypedIndex = 0
				StartTypeInAnimation = true
				initTypingDelay(1)
			}
		}
		if (EnableTypeInAnimation){
			if (StartTypeInAnimation) {
				if (CurretTypedIndex >= currentTitle.length){
					StartTypeInAnimation = false
					CurretTypedIndex = 0
					EnableTypingDelay = false
				} else {
					if (EnableTypingDelay){
						if (TypingDelayCount >= TypingDelayDuration){
							TypingDelayCount = 0
							EnableTypingDelay = false
						} 
						TypingDelayCount ++
						return currentTitle.substring(0, CurretTypedIndex) + IncorrectWord.substring(0, MistakeWordCount) + "|"

					} 
					initTypingDelay(1)
					if (EnableTypingMistake){
						if (StartMistakeWord)
						{	
							if (MistakeWordCount >= TotalMistakewords){
								StartDeleteMistake = true
								StartMistakeWord = false
							} 
							IncorrectWord += PossibleMistakeString[PossibleMistakeString.length * Math.random() | 0]
							MistakeWordCount ++
							return currentTitle.substring(0, CurretTypedIndex)+ IncorrectWord + "|"

						}
						else if (StartDeleteMistake) {

							if (MistakeWordCount <= 0){
								StartDeleteMistake = false
								MistakeWordCount = 0
								return currentTitle.substring(0, CurretTypedIndex) + IncorrectWord.substring(0, MistakeWordCount) + "|"
							}
							MistakeWordCount --
							// console.log(IncorrectWord.substring(0, MistakeWordCount))
							return currentTitle.substring(0, CurretTypedIndex) + IncorrectWord.substring(0, MistakeWordCount) + "|"

						} else {
							if (Math.random() <= Typingmistakechance){
								IncorrectWord = ""
								StartMistakeWord = true
								StartDeleteMistake = false
								TotalMistakewords = MaxMistakeWord * Math.random() | 0
								MistakeWordCount = 0

								return currentTitle.substring(0, CurretTypedIndex) + IncorrectWord + "|"
							}
						}
						CurretTypedIndex ++
						return currentTitle.substring(0, CurretTypedIndex) + "|"
					}
				}
			}

		} 
		if (enableAnimatedScroll & !StartTypeInAnimation){
			if (enablePause){
				if (pauseCounter >= pauseDuration){
					pauseCounter = 0
					enablePause = false
				}
				else {
					pauseCounter += 1
				}
				return currentTitle
			} else if (enableScrollCoolDown){
				if (scrollCooldownCounter >= scrollCooldownduration){
					scrollCooldownCounter = 0
					enableScrollCoolDown = false
				}
				else {
					scrollCooldownCounter += 1
				}
				return displayTitle
			} else if (enablePauseEnd) {
				if (pauseEndCounter >= pauseEndDuration){
					pauseEndCounter = 0
					enablePauseEnd = false
					enablePause = true
				}
				else {
					pauseEndCounter += 1
				}
				return displayTitle
			} else {
				displayTitle = currentTitle.substring(animatedCounter)
				var dummySongTitleScrollSizeX = thisScene.getLayer("DummySongTitleScroll").size.x
				if (dummySongTitleScrollSizeX <= displayLength){
					animatedCounter = 0
					enablePauseEnd = true
				} else {
					animatedCounter ++
					enableScrollCoolDown = true
				}
				return displayTitle
			}
		} else {
			return currentTitle
		}
	} else {
		LastMusicState = CurrentMusicState
	}

}

