var currentState = 0

function ConvertSecondToMinSecTxt(seconds){

	var minutes = new Number(Math.floor(seconds / 60));
	seconds = new Number(Math.floor(seconds - minutes * 60));

	if (seconds < 10){
		seconds = "0" + seconds
	}

	return minutes + ":" + seconds

}

/**
 * @param {MediaPlaybackEvent} event
 */
