	CurrentUpdateMediaTime = event.position
	TotalDuration = event.duration
	PredictedMediaTime = CurrentUpdateMediaTime
	thisLayer.text = ConvertSecondToMinSecTxt(CurrentUpdateMediaTime)
}


export function update(value) {

	if (currentState == MediaPlaybackEvent.PLAYBACK_PLAYING) {
		PredictedMediaTime += engine.frametime
		if (PredictedMediaTime >= TotalDuration){
			PredictedMediaTime = TotalDuration
		}
		return ConvertSecondToMinSecTxt(PredictedMediaTime)
	} else if (currentState == MediaPlaybackEvent.PLAYBACK_PAUSED) {
		//pass
		//console.log("not playing")
	} else {
		//AccumulateFrameTime = 0
		PredictedMediaTime = 0
	}

	return value;
}

