		if (delayCounter >= AnimationDelayDuration){
			delayCounter = 0
			EnableDelay = false
		} else {
			delayCounter ++
		}
	} else if (StartFadeIn){
		if (thisLayer.alpha >= MaxAlpha){
			StartFadeIn = false
			thisLayer.alpha = MaxAlpha

		} else {
			thisLayer.alpha += AlphaStep
			EnableDelay = true
			LastdelayTotalCount ++
		}
	} else if (StartFadeOut){
		if (thisLayer.alpha <= (MinAlpha + 0.01)){
			StartFadeOut = false
			thisLayer.alpha = MinAlpha
		} else {
			thisLayer.alpha -= AlphaStep
			EnableDelay = true
			LastdelayTotalCount ++
		}
	} else{
		if (CurrentMusicState == MediaPlaybackEvent.PLAYBACK_PLAYING){
			if (LastMusicState != CurrentMusicState){
				StartFadeIn = true
				LastMusicState = CurrentMusicState
				LastdelayTotalCount = 0
			} else {
				thisLayer.alpha = MaxAlpha
			}

		} else {
			if (LastMusicState != CurrentMusicState){
				StartFadeOut = true
				LastMusicState = CurrentMusicState
				LastdelayTotalCount = 0
			} else {
				thisLayer.alpha = MinAlpha
			}

		}
	}
}

