// Please note: Do not remove this line or asset references may break.
export let __workshopId = '3187908708';

export var scriptProperties = createScriptProperties()
	.addSlider({
		name: 'maxScale',
		label: 'Max Scale',
		value: 1,
		min: 0.1,
		max: 5,
		integer: false
	})
	.addSlider({
		name: 'minScale',
		label: 'Min Scale',
		value: 0.2,
		min: 0.1,
		max: 5,
		integer: false
	})
	.addSlider({
		name: 'smoothRate',
		label: 'Smooth Rate',
		value: 70,
		min: 0,
		max: 100,
		integer: true
	})
	.finish();


const resolution = 64 // 16,32,64
const audioBuffer = engine.registerAudioBuffers(resolution);
let smoothAudioBuffer = initialSmoothAudioBuffer(); // [0,0,0,0,..] audioAverageSmoothResult

export function update(value) {
	smoothAudio()
	return getAverage(smoothAudioBuffer)
}

// engine.frametime - Returns the time to update the last frame.
function smoothAudio(){
	for(let i=0; i < resolution; i++){
		let volumeDifference = audioBuffer.average[i] - smoothAudioBuffer[i];
		smoothAudioBuffer[i] += volumeDifference * getField('smoothRate') * engine.frametime
		smoothAudioBuffer[i] = setAudioMinMax(smoothAudioBuffer[i]);
	}
}

function getField(nameField=''){
	if (nameField == 'smoothRate') {
		return 15 - scriptProperties[nameField]/10
	}
	return scriptProperties[nameField]
}

function setAudioMinMax(value){
	return Math.max(
			Math.min(
				value, 
				getField('maxScale')),
			getField('minScale'))
}

function initialSmoothAudioBuffer(){
	let list = []
	for(let i=0; i < resolution; i++){
		list.push(0)
	}
	return list
}

function getAverage(...values){
	if(values.length == 1){
		let obj = values[0]
		values = Array.isArray(obj) ? obj : values
	}
	let sum = values.reduce((total, value) => total + value)
	return sum/values.length
}

function checkArrayFrequency(array=[], field) {
	let sum = getAverage(array)
	return sum > scriptProperties[field] ? 1 : 0;
}

function divArray(array=[], amountParts = 1){
	if(!amountParts || amountParts < 2) return array
	let isOdd = (array.length % 2) != 0
	let size = (array.length - (isOdd ? 1 : 0))/amountParts
	let divList = []

	for(let i = 0; i < amountParts; i++){
		if(isOdd && i == amountParts-1){
			divList.push(array.slice(size*i,size*(i+1)+1))
		}else{
			divList.push(array.slice(size*i,size*(i+1)))
		}
	}
	return divList
}

