			// console.log(easterEggsSongTitleTableContent)
			if (typeof easterEggsSongTitleTableContent == 'string'){
				EasterEggTitle = easterEggsSongTitleTableContent

			} else {
				EasterEggTitle = easterEggsSongTitleTableContent[Math.floor(Math.random() * easterEggsSongTitleTableContent.length)]
			}
		} else {

			var easterEggsTableContent = EasterEggsTable[OriginalArtistTitle.toLowerCase()]
			if (typeof easterEggsTableContent == 'string'){
				EasterEggTitle = easterEggsTableContent
			} else {
				EasterEggTitle = easterEggsTableContent[Math.floor(Math.random() * easterEggsTableContent.length)]
			}
		}
		currentTitle = EasterEggTitle
	} else {
		currentTitle = OriginalArtistTitle
	}
	if (currentTitle == ""){
		currentTitle = "???"
	}
}

export function mediaPlaybackChanged(event) {
	if (event.state == 1){
		updateEasterEggsName()
	}	
}

export function applyUserProperties(changedUserProperties) {
        if (changedUserProperties.hasOwnProperty('eastereggsmode')) {
			EasterEggMode = changedUserProperties.eastereggsmode
			updateEasterEggsName()
			var DummyArtistTitle = thisScene.getLayer("DummyArtistTitle")
			DummyArtistTitle.text = currentTitle
		}
}

export function mediaPropertiesChanged(event) {

	SongTitle = event.title

	var tempCurrentTitle = ""
	if (event.artist !== ""){
		tempCurrentTitle = event.artist;
	} else {
		tempCurrentTitle = event.albumArtist
	}

	if (event.albumTitle !== ""){
		// console.log(tempCurrentTitle.indexOf(event.albumTitle))
		if(tempCurrentTitle.includes(event.albumTitle) !== true) {
			tempCurrentTitle += "    "
			tempCurrentTitle += event.albumTitle
		}

	}
	currentTitle = tempCurrentTitle
	OriginalArtistTitle = currentTitle
	updateEasterEggsName()
	thisLayer.text = currentTitle
	displayTitle = currentTitle
	enablePauseAtStart = true
	var DummyArtistTitle = thisScene.getLayer("DummyArtistTitle")
	DummyArtistTitle.text = currentTitle
}

export function update(value) {
	var dummyArtistTitleSizeX = thisScene.getLayer("DummyArtistTitle").size.x
	enableAnimatedScroll = false
	if (dummyArtistTitleSizeX > thisLayer.maxwidth){
		enableAnimatedScroll = true
		if (enablePauseAtStart == true){
			enablePauseAtStart = false
			enablePause = true
		}
	}
	// console.log(thisLayer.maxwidth)
	// console.log(dummyArtistTitleSizeX)
	if (enableAnimatedScroll){
		if (enablePause){
			if (pauseCounter >= pauseDuration){
				pauseCounter = 0
				enablePause = false
			}
			else {
				pauseCounter += 1
			}
			return currentTitle
		} else if (enableScrollCoolDown){
			if (scrollCooldownCounter >= scrollCooldownduration){
				scrollCooldownCounter = 0
				enableScrollCoolDown = false
			}
			else {
				scrollCooldownCounter += 1
			}
			return displayTitle
		} else if (enablePauseEnd) {
			if (pauseEndCounter >= pauseEndDuration){
				pauseEndCounter = 0
				enablePauseEnd = false
				enablePause = true
			}
			else {
				pauseEndCounter += 1
			}
			return displayTitle
		} else {
			displayTitle = currentTitle.substring(animatedCounter)
			var dummyArtistTitleSizeXScroll = thisScene.getLayer("DummyArtistTitleScroll").size.x
			// console.log(dummyArtistTitleSizeXScroll)
			if (dummyArtistTitleSizeXScroll <= displayLength){
				animatedCounter = 0
				enablePauseEnd = true
			} else {
				animatedCounter ++
				enableScrollCoolDown = true
			}

			// if (animatedCounter ++ >= (currentTitle.length - displayLength)){
			// 	animatedCounter = 0
			// 	enablePauseEnd = true
			// } else {
			// 	animatedCounter += 1
			// 	enableScrollCoolDown = true
			// }
			return displayTitle
		}
	} else {
		return currentTitle
	}

}

