		UserProperties = changedUserProperties
		// console.log(UserProperties)
        if (changedUserProperties.hasOwnProperty('scale')) {
			var userPropertiesLayer = thisScene.getLayer("userProperties")
			ScaledLayerPos = new Vec3(HalfX - ((HalfX - DefaultLayerPos.x) * userPropertiesLayer.scale.x),  HalfY - ((HalfY - DefaultLayerPos.y) * userPropertiesLayer.scale.x), thisLayer.origin.z)
			// console.log("scale:", userPropertiesLayer.scale.x)
			// console.log("DefaultLayerPos.x", DefaultLayerPos.x)
			// console.log("ScaledLayerPos", ScaledLayerPos.y)
			// console.log("HalfX", HalfX)
		}
		if (changedUserProperties.hasOwnProperty('animationalphastepsize')) {
			AlphaStep = UserProperties.animationalphastepsize
			// console.log("AlphaStep:", AlphaStep)
		}
		if (changedUserProperties.hasOwnProperty('animationdelayduration')) {
			AnimationDelayDuration = UserProperties.animationdelayduration
			// console.log("AnimationDelayDuration:", AnimationDelayDuration)
		}
		if (changedUserProperties.hasOwnProperty('animationalphaposstepsize')) {
			AlphaPosStep = UserProperties.animationalphaposstepsize
			// console.log("AlphaPosStep:", AlphaPosStep)
		}
		if (changedUserProperties.hasOwnProperty('animationmaxalpha')) {
			MaxAlpha = changedUserProperties.animationmaxalpha
			// console.log("MaxAlpha:", MaxAlpha)
		}
}

