	CurrentMusicState = event.state
}

export function update(value) {

	if (EnableDelay){
		if (delayCounter >= AnimationDelayDuration){
			delayCounter = 0
			EnableDelay = false
		} else {
			delayCounter ++
		}
	} else if (StartFadeIn){
		if (thisLayer.alpha >= MaxAlpha){
			StartFadeIn = false
			thisLayer.alpha = MaxAlpha

		} else {
			thisLayer.alpha += AlphaStep
			EnableDelay = true
			// thisLayer.origin = new Vec3(thisLayer.origin.x, thisLayer.origin.y - AlphaPosStep, thisLayer.origin.z)
			LastdelayTotalCount ++
		}
	} else if (StartFadeOut){
		if (thisLayer.alpha <= (MinAlpha + 0.01)){
			StartFadeOut = false
			thisLayer.alpha = MinAlpha
		} else {
			thisLayer.alpha -= AlphaStep
			EnableDelay = true
			// thisLayer.origin = new Vec3(thisLayer.origin.x, thisLayer.origin.y + AlphaPosStep, thisLayer.origin.z)
			LastdelayTotalCount ++
		}
	} else{
		if (CurrentMusicState == MediaPlaybackEvent.PLAYBACK_PLAYING){
			if (LastMusicState != CurrentMusicState){
				StartFadeIn = true
				LastMusicState = CurrentMusicState
				LastdelayTotalCount = 0
			} else {
				thisLayer.alpha = MaxAlpha
				thisLayer.origin = ScaledLayerPos
			}

		} else {
			if (LastMusicState != CurrentMusicState){
				StartFadeOut = true
				LastMusicState = CurrentMusicState
				LastdelayTotalCount = 0
			} else {
				thisLayer.alpha = MinAlpha
				// thisLayer.origin = new Vec3(ScaledLayerPos.x,  ScaledLayerPos.y + LastdelayTotalCount*AlphaPosStep, ScaledLayerPos.z)
			}

		}
	}
	// console.log(LastdelayTotalCount*AlphaPosStep)
}

