'use strict';

var counter = 0;
var startCounter = false;
var cooldownStart = false;
var cooldown = 0;

var cooldownDuration = 1000
var maxActiveDuration = 50
var activeProb = 0.1
var activeDuration = 0
var EnableCRTGlichEffect = false


export function applyUserProperties(changedUserProperties) {
        if (changedUserProperties.hasOwnProperty('displaypanelglicheffect')) {
			EnableCRTGlichEffect = changedUserProperties.displaypanelglicheffect
		}
		if (changedUserProperties.hasOwnProperty('displaypanelglicheffectprob')) {
			activeProb = changedUserProperties.displaypanelglicheffectprob
		}
		if (changedUserProperties.hasOwnProperty('displaypanelglicheffectmaxduration')) {
			maxActiveDuration = changedUserProperties.displaypanelglicheffectmaxduration
		}
		if (changedUserProperties.hasOwnProperty('displaypanelglicheffectcooldownduration')) {
			cooldownDuration = changedUserProperties.displaypanelglicheffectcooldownduration
		}

}

/**
 * @param {Boolean} value - for property 'visible'
 * @return {Boolean} - update current property value
 */

export function update(value) {
	if (EnableCRTGlichEffect){
		if (cooldownStart == true) {
			if (cooldown >= cooldownDuration) {
				cooldownStart = false
				cooldown = 0
				return false
			} else {
				cooldown += 1
			}

		} else if (startCounter == true & counter >= activeDuration) {
			startCounter = false
			counter = 0
			cooldownStart = true
			return false
		} else if (startCounter == true & counter < activeDuration){
			counter += 1
		} else { 
			if (Math.random() < activeProb){
				startCounter = true
				activeDuration = Math.random() * maxActiveDuration
				return true
			}
		}
	}
}

