'use strict';
export let __workshopId = '3010592987';

// Define a Vec3 class with scale method
class Vec3 {
    constructor(x, y, z) {
        this.x = x;
        this.y = y;
        this.z = z;
    }

    subtract(vec) {
        return new Vec3(this.x - vec.x, this.y - vec.y, this.z - vec.z);
    }

    length() {
        return Math.sqrt(this.x * this.x + this.y * this.y + this.z * this.z);
    }

    add(vec) {
        return new Vec3(this.x + vec.x, this.y + vec.y, this.z + vec.z);
    }
}

// Set a default color here: "1, 1, 1" (RGB) means white by default
let color = new Vec3(1, 1, 1);
let targetColor = new Vec3(1, 1, 1);
let transitionSpeed = 0.05;

/**
 * @param {Vec3} value - for property 'clearcolor'
 * @return {Vec3} - update current property value
 */
export function update() {
    // Calculate the difference between the target color and the current color
    let diff = targetColor.subtract(color);

    // If the difference is significant, transition smoothly
    if (diff.length() > 0.01) {
        color.x += diff.x * transitionSpeed;
        color.y += diff.y * transitionSpeed;
        color.z += diff.z * transitionSpeed;
    }

    return color;
}

/**
 * @param {MediaThumbnailEvent} event
 */
export function mediaThumbnailChanged(event) {
    targetColor = event.primaryColor;
}

